mapper.select:
    		select b1.loan_repayment_date,b2.bankinfo_name,b1.unrepay_number 
			from borloaninfo b1,bankinfo b2 
			where b1.bankinfo_id=b2.bankinfo_id and b2.bankinfo_name=#{bankinfoName,jdbcType=VARCHAR}

    		select c.loan_repayment_date,b.bankinfo_name,c.unrepay_number 
			from comloaninfo c,bankinfo b 
			where c.bankinfo_id=b.bankinfo_id and b.bankinfo_name=#{bankinfoName,jdbcType=VARCHAR}
